package com.sytax.class07;

public class forLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * initialization
		 * condition
		 * increment or decrement
		 */
		
		
		// example of lottery
		// for loop -- Most widely loop
		
		
		
		for(int i=1; i<=5; i++) {
			
			System.out.println("Hello");
		}
		
		
		/*
		 *start point
		 *end point 
		 *increment or decrement
		 * 
		 */
		System.out.println("I need to print munber from 1 to 20");
		
		
		
		for (int i=1; i<=20; i++) {
			System.out.print(i+" ");
		}
		
		System.out.println(" I need to print numbers from 10 to 25");
		
		for (int i=10; i<=25; i++) {
			
			System.out.print(i+" ");
		}
		System.out.println(" Ineed to print numbers from 50 to 1");
		
		for (int i=50; i>=1; i--) {
			System.out.print(i+" ");
		System.out.println("___________________");
		
		int num =0;
		  while(num<=7){
		    num++;
		    System.out.println(num+" ");
		  }
		  }
	}
}

